  countDownTimer() async {
    for (int x = 90; x > 0; x--) {
      await Future.delayed(Duration(seconds: 1)).then((_) {
        setState(() {
          timerCount -= 1;
          if (timerCount == 0) {
            textText = "";
          }
        });
      });
    }
  }

  countDownTimer1() async {
