  o2packet() {
    shutdownChannel.invokeMethod('getBatteryLevel').then((result) async {
      counter = counter + 1;
      List<int> resList = [0, 20, 0, 15];
      if (counter <= 250) {
        setState(() {
          resList.add((result & 0x00FF));
          resList.add(counter);
          if (_iso2High == true) {
            resList.add(2);
          } else {
            resList.add(1);
          }
        });
        if (_status == "Connected") {
          sendDataUsbConnection(resList, 2);
        }
      } else {
        setState(() {
          counter = 0;
        });
      }
    });
  }

